p = Problem.create :title => 'a + b', :body => "Given <b>a</b> and <b>b</b>, print out their sum!", :time_limit => 1

p.solution_tests.create :got => "1 5\n", :expected => "6\n"
p.solution_tests.create :got => "296618450 7057439\n", :expected => "303675889\n"

p = Problem.create :title => 'a!', :body => "Given <b>a</b>. Print out its factorial!", :time_limit => 1

p.solution_tests.create :got => "1\n", :expected => "1\n"
p.solution_tests.create :got => "2\n", :expected => "2\n"
p.solution_tests.create :got => "3\n", :expected => "6\n"
p.solution_tests.create :got => "4\n", :expected => "24\n"
p.solution_tests.create :got => "5\n", :expected => "120\n"
p.solution_tests.create :got => "6\n", :expected => "720\n"
p.solution_tests.create :got => "50\n", :expected => "30414093201713378043612608166064768844377641568960512000000000000\n"
p.solution_tests.create :got => "500\n", :expected => "\n"
p.solution_tests.create :got => "10\n", :expected => "3628800\n"

c = Compiler.create :title => "g++", :run_command => "$BINARY_PATH"

c.build_steps.create :command => "mv $SOURCE_PATH $SOURCE_PATH.cpp"
c.build_steps.create :command => "g++ $SOURCE_PATH.cpp -o $BINARY_PATH"